<?phpfunction pw_ui_printDialogWrap($dialog = -1) {  if ($dialog == -1) {    StringTools::htmlIndentPrint ("</div>", StringTools::END);    return;  }  if ($dialog == "") {    return false;  }  StringTools::htmlIndentPrint ("<!-- MODALDIALOG START -->");  StringTools::htmlIndentPrint ('<div id="modal"><div class="overlay-decorator"></div><div class="overlay-wrap"><div class="overlay"><div class="dialog-wrap"><div class="dialog" id="dialog"><div class="dialog-decorator"><div id="ajax_dialog">', StringTools::START);  StringTools::htmlIndentPrint ($dialog);  StringTools::htmlIndentPrint ('</div></div></div></div></div></div></div>', StringTools::END);  StringTools::htmlIndentPrint ("<div style='position: absolute; top: 0; left: 0; width: 100%; display: block; height: 100%; overflow: hidden'>");  StringTools::htmlIndentPrint ("<!-- MODALDIALOG END -->", StringTools::START);  return true;}?>