<?phpfunction pw_ui_printDialogWrap($dialog = -1) {  if ($dialog == -1) {    StringTools::htmlIndentPrint ("</div>", StringTools::END);    return;  }  if ($dialog == "") {    return false;  }  StringTools::htmlIndentPrint ("<!-- MODALDIALOG START -->");  StringTools::htmlIndentPrint ('<div id="modal"><div class="overlay-decorator"></div><div class="overlay-wrap"><div class="overlay"><div class="dialog-wrap"><div class="dialog" id="dialog"><div class="dialog-decorator"><div id="ajax_dialog">', StringTools::START);  StringTools::htmlIndentPrint ($dialog);  StringTools::htmlIndentPrint ('</div></div></div></div></div></div></div>', StringTools::END);  StringTools::htmlIndentPrint ("<div style='position: absolute; top: 0; left: 0; width: 100%; display: block; height: 100%; overflow: hidden'>");  StringTools::htmlIndentPrint ("<!-- MODALDIALOG END -->", StringTools::START);  return true;}function pw_ui_getDialogInfo($title, $desc, $href, $method = "post") {  $o  = StringTools::htmlIndent();  $o .= StringTools::htmlIndent("<form method='$method' accept-charset='utf-8' id='form'>", StringTools::START);  $o .= StringTools::htmlIndent("<h1>$title</h1>");  $o .= StringTools::htmlIndent("<div>$desc</div>");  $o .= StringTools::htmlIndent("<div>", StringTools::START);  $o .= StringTools::htmlIndent("<a href='?$href' id='submit'>OK</a>");  $o .= StringTools::htmlIndent("</div>", StringTools::END);  $o .= StringTools::htmlIndent("</form>", StringTools::END);  return $o;}function pw_ui_getDialogQuestion($title, $desc, $byesname, $byestext, $bno, $method = "post") {  $o  = StringTools::htmlIndent();  $o .= StringTools::htmlIndent("<div class='admin'><form method='$method' accept-charset='utf-8' id='form'>", StringTools::START);  $o .= StringTools::htmlIndent("<h1>$title</h1>");  $o .= StringTools::htmlIndent("<div>", StringTools::START);  $o .= $desc;  $o .= StringTools::htmlIndent("</div>", StringTools::END);  $o .= StringTools::htmlIndent("<div>", StringTools::START);  $o .= StringTools::htmlIndent("<input id='submit' type='submit' name='$byesname' value='$byestext' />");  $o .= "<button type='submit'>$byestext</button>";//   $o .= StringTools::htmlIndent("<a href='?$bno'>Cancel</a>");  $o .= "<button type='submit'>Cancel</button>";  $o .= StringTools::htmlIndent("</div>", StringTools::END);  $o .= StringTools::htmlIndent("</form></div>", StringTools::END);  return $o;}?>